pub enum Error {

    /// The supplied address is null.
    NullAddress,

    /// The supplied address range is empty.
    EmptyRange,

    /// The queried memory is free.
    FreeMemory,

    /// Invalid procfs input.
    ProcfsInput,

    /// A system call failed.
    SystemCall(io::Error),

    /// A macOS kernel call failed
    MachRegion(::libc::c_int),
}
